import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [inputWord, setInputWord] = useState('');
  const [selectedOption, setSelectedOption] = useState('gclnk');
  const [processedData, setProcessedData] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!inputWord.trim() || !selectedOption) {
      setError('Пожалуйста, заполните все поля');
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:8000/create-short-link', {
        word: inputWord,
        selectedOption: selectedOption,
      });

      setProcessedData(response.data.result);
      setError(null);
    } catch (error) {
      console.error('Произошла ошибка при отправке запроса:', error);
      setError('Произошла ошибка. Пожалуйста, попробуйте снова.');
    }
  };

  return (
    <div className="app">
      <h1 className="heading">Сокращатель ссылок</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={inputWord}
          onChange={(e) => setInputWord(e.target.value)}
          placeholder="Введите слово"
        />
        <select
          value={selectedOption}
          onChange={(e) => setSelectedOption(e.target.value)}
        >
          <option value="gclnk">gclnk</option>
          <option value="bitly">bitly</option>
        </select>
        <button type="submit">Сократить ссылку</button>
      </form>

      {error && <p className="error">{error}</p>}
      {processedData && <p>Сокращенная ссылка: {processedData}</p>}
    </div>
  );
};

export default App;










